CREATE TABLE BON (NRBON INTEGER NOT NULL, DATA DATE, TOTALBON FLOAT, TOTALTVA FLOAT, CLIENT_ID INTEGER, PRIMARY KEY (NRBON))
CREATE TABLE CLIENT (ID INTEGER NOT NULL, NUME VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE CURATATORIE (COD INTEGER NOT NULL, DTYPE VARCHAR(31), DENUMIRE VARCHAR(255), TARIF FLOAT, CODCURATATORIE INTEGER, TIPCURATARE VARCHAR(255), PRIMARY KEY (COD))
CREATE TABLE LINIEBON (IDLINIE INTEGER NOT NULL, TVALINIE FLOAT, VALOARELINIE FLOAT, BON_NRBON INTEGER, CURATATORIE_COD INTEGER, PRIMARY KEY (IDLINIE))
ALTER TABLE BON ADD CONSTRAINT FK_BON_CLIENT_ID FOREIGN KEY (CLIENT_ID) REFERENCES CLIENT (ID)
ALTER TABLE LINIEBON ADD CONSTRAINT FK_LINIEBON_BON_NRBON FOREIGN KEY (BON_NRBON) REFERENCES BON (NRBON)
ALTER TABLE LINIEBON ADD CONSTRAINT FK_LINIEBON_CURATATORIE_COD FOREIGN KEY (CURATATORIE_COD) REFERENCES CURATATORIE (COD)
CREATE TABLE SEQUENCE (SEQ_NAME VARCHAR(50) NOT NULL, SEQ_COUNT DECIMAL(38), PRIMARY KEY (SEQ_NAME))
